mapper.insert:
INSERT	INTO	ACTION_HIST(
	HIST_DATE
	,REQ_TYPE
	,IP
	,USER_ID
	,EMAIL
	,LOG
	,AS_IS
	,TO_BE
)
VALUES(
	NOW()
	,#{reqType}
	,#{ip}
	,#{userId}
	,#{email}
	,#{log}
	,#{asIs}
	,#{toBe}
)

mapper.select:
SELECT	COUNT(HIST_ID)
FROM	ACTION_HIST
WHERE	HIST_DATE >=STR_TO_DATE(#{strDate}, '%Y-%m-%d %H:%i:%S')
AND		HIST_DATE <= STR_TO_DATE(#{endDate}, '%Y-%m-%d %H:%i:%S')

AND		REQ_TYPE = #{requestType}


AND		IP = #{ip}


AND		EMAIL = #{email}


AND		LOG LIKE CONCAT('%', #{log}, '%')


AND		AS_IS LIKE CONCAT('%', #{asIs}, '%')


AND		TO_BE LIKE CONCAT('%', #{toBe}, '%')

SELECT * FROM ( SELECT HIST_ID histId ,HIST_DATE hisDate ,REQ_TYPE reqType ,IP ,USER_ID userId ,EMAIL ,LOG ,AS_IS asIs ,TO_BE toBe FROM ACTION_HIST WHERE HIST_DATE >=STR_TO_DATE(#{strDate}, '%Y-%m-%d %H:%i:%S') AND HIST_DATE <= STR_TO_DATE(#{endDate}, '%Y-%m-%d %H:%i:%S')
AND REQ_TYPE = #{requestType}
AND IP = #{ip}
AND EMAIL = #{email}
AND LOG LIKE CONCAT('%', #{log}, '%')
AND AS_IS LIKE CONCAT('%', #{asIs}, '%')
AND TO_BE LIKE CONCAT('%', #{toBe}, '%')
ORDER BY HIST_ID DESC LIMIT #{startRow}, #{endRow} ) A ORDER BY histId DESC
