mapper.update:
UPDATE	COMMUNITY
SET		VIEW_COUNT= VIEW_COUNT +1
WHERE ID = #{id}

UPDATE	COMMUNITY
SET		RECOMMEND_COUNT= RECOMMEND_COUNT +1
WHERE ID = #{id}

UPDATE	COMMUNITY
SET		WRITE_DATE= SYSDATE

		,TITLE= #{title}


		,REQUEST_IP= #{requestIp}


		,BODY= #{body}

		,DISPLAY_FILE_NAME= #{displayFilename}
WHERE	ID= #{id}
AND 	USER_ID= #{userId}

mapper.select:
SELECT COUNT(ID)
FROM COMMUNITY
WHERE USER_ID= #{userID}

mapper.delete:
DELETE
FROM	COMMUNITY

WHERE	USER_ID= #{userId}
AND		ID IN

	#{id}
